document.querySelectorAll('.problem-card').forEach((card, index) => {
    card.addEventListener('click', () => {
        const solution = document.getElementById(`solution-${index}`);
        const arrow = card.querySelector('span');

        const isActive = solution.classList.contains('active');


        document.querySelectorAll('.solution-card.active').forEach(activeSolution => {
            if (activeSolution !== solution) {
                activeSolution.classList.remove('active');
                const problemCard = activeSolution.previousElementSibling;
                problemCard.querySelector('span').style.transform = 'rotate(0deg)';
                problemCard.classList.remove('bg-green-50');
            }
        });


        solution.classList.toggle('active');
        if (solution.classList.contains('active')) {
            arrow.style.transform = 'rotate(180deg)';
            card.classList.add('bg-green-50');
        } else {
            arrow.style.transform = 'rotate(0deg)';
            card.classList.remove('bg-green-50');
        }
    });
});


