document.addEventListener('DOMContentLoaded', () => {
    const swatches = document.querySelectorAll('.color-swatch');
    const demoArea = document.getElementById('demo-area');
    const demoCard = document.getElementById('demo-card');
    const demoTitle = document.getElementById('demo-title');
    const demoText = document.getElementById('demo-text');
    const demoButton = document.getElementById('demo-button');

    swatches.forEach(swatch => {
        swatch.addEventListener('click', () => {
            const color = swatch.dataset.color;
            const use = swatch.dataset.use;

            switch (use) {
                case 'background':
                    demoArea.style.backgroundColor = color;
                    break;
                case 'card-background':
                    demoCard.style.backgroundColor = color;
                    break;
                case 'accent':
                    demoCard.style.borderColor = color;
                    demoCard.classList.add('border-l-8');
                    break;
                case 'text':
                    demoTitle.style.color = color;
                    demoText.style.color = color;
                    break;
                case 'cta':
                    demoButton.style.backgroundColor = color;
                    break;
                case 'default-text':
                     demoTitle.style.color = color;
                     demoText.style.color = color;
                     break;
            }
        });
    });

    // Smooth scrolling for navigation
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            const targetElement = document.querySelector(this.getAttribute('href'));
            if (targetElement) {
                targetElement.scrollIntoView({
                    behavior: 'smooth'
                });
            }
        });
    });
});

