function showInfo(name, role, value, color) {
    const modal = document.getElementById('info-modal');
    document.getElementById('info-name').textContent = name;
    document.getElementById('info-role').textContent = role;
    document.getElementById('info-value').textContent = value;
    document.getElementById('info-color').textContent = color;
    modal.style.display = 'block';
}

function hideInfo() {
    document.getElementById('info-modal').style.display = 'none';
}

function showDetails(charJson) {
    const char = JSON.parse(charJson);
    const modalOverlay = document.getElementById('info-modal-overlay');

    document.getElementById('modal-name').textContent = char.name;
    document.getElementById('modal-role').textContent = char.role;
    document.getElementById('modal-size').textContent = char.size;
    document.getElementById('modal-color-name').textContent = char.color;
    document.getElementById('modal-accent-name').textContent = char.accent;

    modalOverlay.style.display = 'flex';
}

function hideDetails() {
    document.getElementById('info-modal-overlay').style.display = 'none';
}

