let currentPageIndex = 0;
const pageDisplay = document.getElementById('page-display');
const sceneTitle = document.getElementById('scene-title');
const sceneDescription = document.getElementById('scene-description');
const promptBox = document.getElementById('prompt-box');
const promptText = document.getElementById('prompt-text');
const prevButton = document.getElementById('prev-page');
const nextButton = document.getElementById('next-page');
const paginationDots = document.getElementById('pagination-dots');

function updatePageDisplay() {
    const scene = scenes[currentPageIndex];

    // ุชุญุฏูุซ ุชุตููู ุงูุตูุญุฉ
    pageDisplay.style.backgroundImage = scene.bg ? `linear-gradient(${scene.bg})` : '';
    pageDisplay.style.borderColor = scene.color;
    pageDisplay.style.color = scene.textCol || 'var(--dark)';
    pageDisplay.querySelector('.absolute.bottom-4').style.color = scene.textCol || 'var(--dark)';

    // ุชุญุฏูุซ ุงููุญุชูู ุงููุตู
    sceneTitle.textContent = `${scene.id}. ${scene.title}`;
    sceneDescription.textContent = scene.desc;

    // ุชุญุฏูุซ ูุฑุจุน ุงูุชูุฌูู
    if (scene.prompt) {
        promptBox.classList.remove('hidden');
        promptText.textContent = scene.prompt;
        promptBox.style.backgroundColor = scene.textCol === 'var(--light)' ? 'rgba(74, 74, 74, 0.9)' : 'rgba(254, 252, 243, 0.9)';
        promptBox.style.color = scene.textCol === 'var(--light)' ? 'var(--light)' : 'var(--dark)';
    } else {
        promptBox.classList.add('hidden');
    }

    // ุชุญุฏูุซ ูุคุดุฑุงุช ุงูุตูุญุงุช
    document.querySelectorAll('.pagination-dot').forEach((dot, index) => {
        dot.classList.remove('active');
        dot.style.backgroundColor = (index === currentPageIndex) ? 'var(--accent)' : 'var(--secondary)';
    });

    // ุชุญุฏูุซ ุฃุฒุฑุงุฑ ุงูุชููู
    prevButton.disabled = currentPageIndex === 0;
    nextButton.disabled = currentPageIndex === scenes.length - 1;
    prevButton.style.opacity = currentPageIndex === 0 ? 0.5 : 1;
    nextButton.style.opacity = currentPageIndex === scenes.length - 1 ? 0.5 : 1;
}

function createPaginationDots() {
    scenes.forEach((_, index) => {
        const dot = document.createElement('button');
        dot.classList.add('pagination-dot', 'w-3', 'h-3', 'rounded-full', 'mx-1');
        dot.addEventListener('click', () => {
            currentPageIndex = index;
            updatePageDisplay();
        });
        paginationDots.appendChild(dot);
    });
}

prevButton.addEventListener('click', () => {
    if (currentPageIndex > 0) {
        currentPageIndex--;
        updatePageDisplay();
    }
});

nextButton.addEventListener('click', () => {
    if (currentPageIndex < scenes.length - 1) {
        currentPageIndex++;
        updatePageDisplay();
    }
});

createPaginationDots();
updatePageDisplay();

