const cards = document.querySelectorAll('.card-example');
const modalOverlay = document.getElementById('info-modal-overlay');

cards.forEach(card => {
    card.addEventListener('click', function() {
        const title = this.getAttribute('data-title');
        const tip = this.getAttribute('data-tip');

        document.getElementById('modal-title').textContent = title;
        document.getElementById('modal-tip').textContent = tip;

        const groupColor = window.getComputedStyle(this).getPropertyValue('background-color');
        document.getElementById('info-modal-content').style.borderColor = groupColor;

        modalOverlay.style.display = 'flex';
    });
});

function hideDetails() {
    modalOverlay.style.display = 'none';
}

